import { call, put, takeEvery } from "redux-saga/effects";
import registerUser from '../../components/regestration'


export function* doRegistration(): IterableIterator<any>{
    yield takeEvery(`@@login/DO_LOGIN`, function*(action: any) {
        try {
            //1st step
        
            //2nd step
            yield put({type: 'ADD_OBJ', obj: {email: action}});
        } catch(error) {
            yield put({type: 'FETCH_FAILED', error});
        }
    })
}
