import React from 'react';
import { Link } from 'react-router-dom';



export default class RegistrationComponent extends React.Component{
    state:any = {
        firstname: '',
        secondname: '',
        email: '',
        password: '',
        errorFirstname: '',
        errorSecondname: '',
        errorEmail: '',
        errorPassword: ''
    }
    
    submitRegistration = ()=>{
        const inpRegExpr = new RegExp(/[a-zA-Z]{3,}/i);
        const passWordExpr = new RegExp(/[a-zA-Z0-9]{3,}/i);
        const emailRegExpr = new RegExp(/^[(]?[0-9]{3}[)]?[-\s.]?[0-9]{3}[-/\s.]?[0-9]{4}$/);
        console.log(JSON.stringify(this.state)) 
        
        if(!inpRegExpr.test(this.state.firstname)){
            this.setState({errorFirstname: 'ErrorText: только '} as any)
        }
        if(!inpRegExpr.test(this.state.secondname)){
           
        }
        if(!emailRegExpr.test(this.state.email)){
            
        }
        if(!passWordExpr.test(this.state.password)){
            
        }
        

        fetch ('http://localhost:3000/users',{
            method: 'POST',
            body: JSON.stringify(this.state),
            headers:{
                "Content-type": "application/json"
            }
        })

        // setTimeout(()=>{
        //     fetch('http://localhost:3000/users')
        //     .then(response => response.json())
        //     .then(result =>{
        //         console.log(result)
        // })
        // }, 1000)
      
    }
   
    handle = (event:any)=>{
            this.setState({[event.target.name]:event.target.value} as any)
        console.log(this.state.firstname)
    }

    


    render(){
        return(
            <div className="containerRegestration">
                <div className="conteiner-form">
                    <form action="#" method="GET">
                        <div className="item-firstname">
                            <div className="firstname-left">FirstName:</div>
                            <div className="firstname-right">
                                <input type="text" name="firstname" onChange = {this.handle} value={this.state.firstname} />
                            </div>
                        </div>
                        <div id="firstname">{this.state.errorFirstname}</div>
                        <div className="item-secondname">
                            <div className="secondname-left">SecondName:</div>
                            <div className="secondname-right">
                                <input type="text" name="secondname" onChange = {this.handle} value={this.state.secondname}/>
                            </div>
                        </div>
                        <div className="item-email">
                            <div className="email-left">Email:</div>
                            <div className="email-right">
                                <input type="email" name="email" onChange = {this.handle} value={this.state.email}/>
                            </div>
                        </div>
                        <div className="item-password">
                            <div className="password-left">Password:</div>
                            <div className="password-right">
                                <input type="password" name="password" onChange = {this.handle} value={this.state.password}/>
                            </div>
                        </div>
                        <div className="item-button">
                            <button onClick={() => this.submitRegistration()} id="submit-registr">Registration</button>
                        </div>
                    </form>
                </div>
                <div>
                    <Link to="/">На главную</Link>
                </div>
            </div>
        )
    }
}
